import React, { Component } from 'react'
import dudley from "../music/dudley.mp3"
import tmg from "../music/tmg.mp3"
import zelda from "../music/zelda.mp3"
import Forward from "./forward"
import Backward from "./backward"
import vinyl from "../Photos/vinyl.png"
import "./play.css"

var audio = document.getElementById("audio")
var is_on = false
export class play extends Component {
    state = {
        index: 0,
        song_lst : [dudley, tmg, zelda],
    }

    play = (song) =>{
        audio = document.getElementById("audio")
        var vin = document.getElementById("vinyl")
        audio.src = song
        if(is_on){
            audio.pause()
            // this.setState({song_on: false})
            is_on = false
            vin.setAttribute("style", "animation-play-state: paused;");



        }
        else{
            // this.setState({song_on: true})
            is_on = true;
            audio.play()
            console.log("playing")

        }
    }

    forward = () => {
        // this.setState({song_on: false})
        is_on = false;
        var index = this.state.index + 1
        this.setState({index: index % 3})
        this.play(this.state.song_lst[index % 3])
        // audio.play()
        console.log(index % 3)
        console.log(audio.src)


    }

    render() {
        return (
            <div>
                <img src={vinyl} alt="VinylS" id="vinyl"/>
                <div onClick={() => this.play(this.state.song_lst[this.state.index])} className="play"/>
                <audio src="" id="audio" controls />
                <Forward onClick={this.forward}/>
                <Backward/>
            </div>
        )
    }
}

export default play
