import React, { Component } from 'react'
import dudley from "../music/dudley.mp3"
import tmg from "../music/tmg.mp3"
import zelda from "../music/zelda.mp3"
import Forward from "./forward"
import Backward from "./backward"
import "./play.css"

var audio = document.getElementById("audio")

export class play extends Component {
    state = {
        index: 0,
        song_lst : [dudley, tmg, zelda],
        song_on: false
    }

    play = (song) =>{
        audio = document.getElementById("audio")
        audio.src = song
        if(this.state.song_on){
            audio.pause()
            this.setState({song_on: false})
            console.log("pause")

        }
        else{
            audio.play()
            this.setState({song_on: true})
            console.log("playing")
        }
    }

    forward = () => {
        this.setState({index: (this.state.index + 1) % 3})
        this.play(this.state.song_lst[this.state.index + 1])
        this.setState({song_on: false})
        // audio.play()
        console.log(this.state.index)
        console.log(audio.src)


    }

    render() {
        return (
            <div>
                <div onClick={() => this.play(this.state.song_lst[this.state.index])} className="play"/>
                <audio src="" id="audio" controls />
                <Forward onClick={this.forward}/>
                <Backward/>
            </div>
        )
    }
}

export default play
