import React, { Component } from 'react'
import dudley from "../music/dudley.mp3"
import tmg from "../music/tmg.mp3"
import zelda from "../music/zelda.mp3"
import Forward from "./forward"
import Backward from "./backward"
import "./play.css"

export class play extends Component {
    state = {
        index: 0,
        song_lst : [dudley, tmg, zelda],
        audio: document.getElementById("audio")

    }


    play = () =>{
        const audio = this.state.audio
        audio.play()
    }

    forward = () => {
        const audio = document.getElementById("audio")
        this.index = this.index % 3
        audio.play()
    }

    render() {
        return (
            <div>
                <div onClick={this.play} className="play"/>
                <audio src={this.state.song_lst[this.state.index]} id="audio" controls autoPlay/>
                <Forward onClick={this.forward}/>
                <Backward/>
            </div>
        )
    }
}

export default play
