import React, { Component } from 'react'
import dudley from "../music/dudley.mp3"
import Forward from "./forward"
import Backward from "./backward"
import "./play.css"

export class play extends Component {
    click = () =>{
        const audio = document.getElementById("audio")
        audio.play()
    }

    render() {
        return (
            <div>
                <div onClick={this.click} className="play"/>
                <audio src={dudley} id="audio" controls autoPlay/>
                <Forward/>
                <Backward/>
            </div>
        )
    }
}

export default play
