import React, { Component } from 'react'
import { Col, Row, Button, Form, FormGroup, Label, Input, ButtonToggle } from 'reactstrap';
import "./login.css"
import axios from "axios"
import {getFromStorage, setInStorage} from "../../utils/storage"



export class login extends Component {
    state = {
        token:'',
        isLoading:false
    }

    click = (e) => {
        e.preventDefault();
        const User = {
            Email:document.getElementById("Email").value, 
            Password:document.getElementById("password").value,
            }
        // console.log(User)
        axios.post("http://localhost:5000/signin", User) //this 
        .then(res => console.log(res.data)); 

        // Grab state
        const {signInEmail,signInPassword,} = User;
        this.setState({
            isLoading: true,
        });
        // Post request to backend
        fetch('../../api/routes/signin', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify({
            Email: signInEmail,
            Password: signInPassword,
            }),
        }).then(res => res.json())
            .then(json => {
            console.log('json', json);
            if (json.success) {
                setInStorage('the_main_app', { token: json.token });
                this.setState({
                signInError: json.message,
                isLoading: false,
                signInPassword: '',
                signInEmail: '',
                token: json.token,
                });
            } else {
                this.setState({
                signInError: json.message,
                isLoading: false,
                });
            }
            });
        // window.location = '/';
    }
    render() {
        return (
            <div>
                <div className="Login">
                <h1>Login in</h1>
                {/* <p>____________________________________________________________</p> */}
                <hr></hr>                
                <br/>
                <Form>
                    <h3>Email</h3> 
                    <Row form>
                        <Col md={12}>
                        <FormGroup>
                            <Input type="email" name="email" id="Email" placeholder="Enter your Email..." />
                        </FormGroup>
                        </Col>
                    </Row>
                    <br/>
                    <h3>Password</h3>
                    <Row form>
                        <Col md={12}>
                        <FormGroup>
                            <Input type="password" name="email" id="password" placeholder="Enter your Password..." />
                        </FormGroup>
                        </Col>
                    </Row>
                    <br/>
                    <Button onClick={this.click} type="submit" color="primary" size="lg" block>Sign in</Button>
                </Form>

            </div>

            </div>
        )
    }
}

export default login
