import React, { Component } from 'react'
import {Link} from "react-router-dom"
import { Col, Row, Button, Form, FormGroup, Label, Input, ButtonToggle } from 'reactstrap';
import "./login.css"
import axios from "axios"
import {getFromStorage, setInStorage} from "../../utils/storage"
import { Redirect } from 'react-router-dom'
import Nav from "../nav2/nav2"

export class login extends Component {
    state = {
        isLoading:'',
        token:'',
        signInError:'',
        signInEmail:'',
        signInPassword:'',
        signUpEmail:'',
        signUpPassword:'',
        signUpError:'',
    }

    click = (e) => {
        e.preventDefault();
        const User = {
            Email:document.getElementById("Email").value, 
            Password:document.getElementById("password").value
        }
        //once logged in save the user token in local stroage
        axios.post("http://localhost:5000/signin", User) //this 
        .then(res =>  {
            // console.log('json',res.data);
            if(res.data.success){
                console.log("valid user. Token saved locally");
                setInStorage('the_main_app', {token: res.data.token});
                this.props.history.push("/search")


            }
        })

        // })
        // window.location = '/';
    }
    render() {
        const obj = getFromStorage('the_main_app');    
        if (obj && obj.token){
            //verify token
            console.log("token not null")
            const token = {token: obj.token}

            // var status = true;
            axios.post("http://localhost:5000/verify", token)
            // .then(res => localStorage.setItem("valid", JSON.stringify(res.data.Success)));
            .then(res => {if(res.data.Success === true){console.log("true")}});

            // if(JSON.parse(localStorage.getItem('valid')) === true){
            //     console.log("valid token")
            //     return(<Redirect to="/search"></Redirect>)
            //     // this.forceUpdate();
            // }


        }
        return (
            <div>
                <Nav></Nav>
                <div className="Login">
                <h1>Login in</h1>
                {/* <p>____________________________________________________________</p> */}
                <hr></hr>                
                <br/>
                <Form>
                    <h3>Email</h3> 
                    <Row form>
                        <Col md={12}>
                        <FormGroup>
                            <Input type="email" name="email" id="Email" placeholder="Enter your Email..." />
                        </FormGroup>
                        </Col>
                    </Row>
                    <br/>
                    <h3>Password</h3>
                    <Row form>
                        <Col md={12}>
                        <FormGroup>
                            <Input type="password" name="email" id="password" placeholder="Enter your Password..." />
                        </FormGroup>
                        </Col>
                    </Row>
                    <br/>
                    <Button onClick={this.click} type="submit" color="primary" size="lg" block>Sign in</Button>
                </Form>

            </div>

            </div>
        )
    }
}

export default login
